function App() {

   searchHandler = () => {
      window.alert("El ID que voy a buscar")
   }
   return (
      <div className='App'>
         <SearchBar onSearch={(characterID) => window.alert(characterID)} />
         <Cards characters={characters} onClose={() => window.alert('Emulamos que se cierra la card')} />

      </div>
   );
}

export default App;
